import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Redirect, useHistory } from "react-router";
import "./css/loginSignup.css";
export default function Signup() {
  const selector = useSelector((state) => state.home);
  const dispatch = useDispatch();
  const history = useHistory();
  const [state, setState] = useState({
    fname: "",
    lname: "",
    email: "",
    password: "",
    address: "",
  });
  const changeInput = (e) => {
    // console.log(e.target.name,e.target.value);
    setState({ ...state, [e.target.name]: e.target.value });
  };
  const submitButton = () => {
    if(localStorage.getItem(state.email)!=undefined){
        dispatch({ type: "signup", payload: state });
        history.push("/login");
    }else{
        alert("this email already exist please try another email")
        history.push("/signup")
    }
    setState({ fname: "", lname: "", email: "", password: "", address: "" });
  };
  return (
    <div className="login signup ">
      <h1>SignUp</h1>
      <div>
        <h5>First Name</h5>
        <input
          name="fname"
          type="text"
          value={state.fname}
          class="form-control"
          placeholder="fname"
          onChange={changeInput}
        />
      </div>
      <div>
          <h5>Last name</h5>
      <input
        name="lname"
        type="text"
        value={state.lname}
        class="form-control"
        placeholder="lname"
        onChange={changeInput}
      />

      </div>
      <div>
          <h5>Email</h5>
      <input
        name="email"
        type="email"
        value={state.email}
        class="form-control"
        placeholder="email"
        onChange={changeInput}
      />
      </div>
      <div>
          <span>Password</span>
      <input
        name="password"
        value={state.password}
        type="password"
        class="form-control"
        placeholder="password"
        onChange={changeInput}
      />
      </div>
      <div>
          <h5>Address</h5>
      <input
        name="address"
        type="text"
        value={state.address}
        class="form-control"
        placeholder="address"
        onChange={changeInput}
      />
      </div>

      <button className="btn btn-danger" onClick={submitButton}>
        submit
      </button>
    </div>
  );
}
