import data from "../groceries/data"
let initialState={
    data:data,
    product:data,
    search:"",
    qty:0,
    cart:[]
}
const home=(state=initialState,action)=>{
    // console.log(action.payload);
    switch (action.type) {
        case "addInBasket":
            let arr=[...state.data]
            let basketObj=[]
            let isPresent=state.data.find(obj=>{
                return obj.id==action.payload.id
            })

           for(let i=0;i<state.cart.length;i++){
               let obj=state.cart[i]
               if(obj.id==action.payload){
                   obj.qty++
               }
           }
            return {
                ...state,
                data:arr,
                qty:state.qty+1,
                cart:basketObj
            }
        case "removeFromBasket":
            console.log(action);
        default:
            return state
    }

}
export default home
