import React, { useState } from "react";
import { useSelector } from "react-redux";
import "./css/basket.css";
export default function Cart({ addBasket, removeBasket }) {
  const select = useSelector((state) => state.home);
  const data = select.cart;
  const [quant, setQuant] = useState(0);
  // useEffect
  return (
    <div className="basketPage">
      <div className="cartItems">
        <h1>Cart</h1>
        {data.map((obj) => {
          return (
            <div className="catagoryItems">
              <img src={obj.filename} />
              <h6>{obj.title}</h6>
              <div className="price">
                <span>
                  <b>price</b> : {obj.price}
                </span>
                <div className="quantity">
                  <h6>Qty : </h6>
                </div>
                <div className="addRemove">
                  <button
                    onClick={() => removeBasket(obj)}
                    className="btn btn-danger addCart"
                  >
                    -
                  </button>
                  <h6>{obj.qty}</h6>
                  <button
                    onClick={() => addBasket(obj)}
                    className="btn btn-primary addCart add"
                  >
                    +
                  </button>
                </div>
              </div>
            </div>
          );
        })}
      </div>
      <div className="amountDetail">
        <h1>dj</h1>
        <table class="table">
          <thead>
            <tr>
              <th scope="col">Item Name</th>
              <th scope="col">Price </th>
              <th scope="col">QTY</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <th scope="row">1</th>
              <td>Mark</td>
              <td>Otto</td>
              <td>@mdo</td>
            </tr>
            <tr>
              <th scope="row">2</th>
              <td>Jacob</td>
              <td>Thornton</td>
              <td>@fat</td>
            </tr>
            <tr>
              <th scope="row">3</th>
              <td colspan="2">Larry the Bird</td>
              <td>@twitter</td>
            </tr>
          </tbody>
        </table>
        <div className="amountItem">
          {data.map((obj) => {
            return (
              <>
                <div className="amountItemPrice">
                  <h3>{obj.title}</h3>
                  <span>{obj.price}</span>
                </div>
              </>
            );
          })}
        </div>
      </div>
    </div>
  );
}
