import data from "../groceries/data"
let initialState={
    data:data,
    search:"",
    qty:0,
    basket:[]
}
const home=(state=initialState,action)=>{
    // console.log(action.payload);
    switch (action.type) {
        case "addInBasket":
            let arr=[]
            let basketObj=[...state.basket,action.payload]
            for(let i=0;i<state.data.length;i++){
                let obj=state.data[i]
                if(obj["id"]==action.payload["id"]){
                    // console.log(obj["id"], action.payload["id"]);
                    obj["qty"]=1
                    console.log(obj);
                    arr[i]=obj
                }else{
                    arr[i]=obj
                }
            }
            // console.log(arr);

            return {
                ...state,
                data:arr,
                qty:state.qty+1,
                basket:basketObj
            }
        case "removeFromBasket":
            console.log(action);
        default:
            return state
    }

}
export default home
