import React from 'react';
import logo from './logo.svg';
// import { Counter } from './features/counter/Counter';
import './App.css';
import Home from './component/Home';
import Heading from './component/Heading';
import { BrowserRouter as Router,Route,Switch } from 'react-router-dom';
// import Basket from './component/Basket';
import Login from './component/Login';
import Signup from './component/Signup';
import Catagory from './component/Catagory';
import { useSelector } from 'react-redux'
import { useDispatch } from 'react-redux'
import Cart from './component/Cart';
function App() {
  const dispatch=useDispatch()
  const addBasket=(obj)=>{
    dispatch({
       type:"addInBasket",
       payload:obj
    })
}
const removeBasket=obj=>{
  dispatch({
    type:"removeFromBasket",
    payload:obj
 })
}
  return (
    <Router>
    <div className="App">
    <Heading/>
      <Switch>
      <Route exact path="/"><Home removeBaske={removeBasket}addBasket={addBasket}></Home> </Route>
      <Route exact path="/cart"><Cart addBasket={addBasket}/></Route>
      <Route path="/login"><Login/></Route>
      <Route path="/signup"><Signup/></Route>
      <Route path="/catagory/:name"><Catagory addBasket={addBasket}/></Route>
      </Switch>
    </div>

    </Router>
  );
}

export default App;
