import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { useDispatch } from 'react-redux'
// import App from '../App';
// import data from '../groceries/data';ii
import "./css/Home.css"
export default function Home() {
    let state=useSelector(state=>state.home)
    let dispatch=useDispatch()
    const addBasket=(obj)=>{
        dispatch({
           type:"basketQty",
           payload:obj.qty
        })
    }
    useEffect(()=>{
        for(let i=0;i<state.data.length;i++){
            state.data[i]["qty"]=0
        }
    },[])
    const allGroceries=[...state.data]
    const [qty,setQty]=useState(0) 
    return (
        <div className="mainContainer">
           <div className="catagory">
                <div className="catagoryList"></div>
                <div className="catagoryList"></div>
                <div className="catagoryList"></div>
                <div className="catagoryList"></div>
                <div className="catagoryList"></div>
           </div>
           <div className="groceryList">
               <h3>Top Rated</h3>
               <div className="list">
               {allGroceries.map((obj,index)=>{
                //    console.log(obj);
                   return(
                       <div className="item">

                       <img src={obj.filename}/>
                       <h6>
                           {obj.title}
                       </h6>
                    <div className="price">
                       <span>price : {obj.price}</span>
                       <div className="quantity">
                            <span>Qty : <input value={obj["qty"]} type="number"/></span>
                       </div>
                            <button onClick={()=>addBasket(obj)}className="btn btn-secondary addBasket"> add 
                            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-cart2" viewBox="0 0 16 16">
                                <path d="M0 2.5A.5.5 0 0 1 .5 2H2a.5.5 0 0 1 .485.379L2.89 4H14.5a.5.5 0 0 1 .485.621l-1.5 6A.5.5 0 0 1 13 11H4a.5.5 0 0 1-.485-.379L1.61 3H.5a.5.5 0 0 1-.5-.5zM3.14 5l1.25 5h8.22l1.25-5H3.14zM5 13a1 1 0 1 0 0 2 1 1 0 0 0 0-2zm-2 1a2 2 0 1 1 4 0 2 2 0 0 1-4 0zm9-1a1 1 0 1 0 0 2 1 1 0 0 0 0-2zm-2 1a2 2 0 1 1 4 0 2 2 0 0 1-4 0z"/>
                            </svg>
                        </button>
                       {/* <button className="btn btn-secondary"> add</button> */}
                    </div>
                    </div>
                   )
               })}

               </div>
           </div>

        </div>
    )
}
