import { act } from "react-dom/test-utils"
import data from "../groceries/data"
let initialState={
    data:data,
    product:data,
    search:"",
    qty:0,
    cart:[]
}
const home=(state=initialState,action)=>{
    // console.log(action.payload);
    switch (action.type) {
        case "addInBasket":
            let arr=[]
            let basketObj=[]
            let isPresent=state.cart.find(obj=>{
                return obj.id==action.payload.id
            })  
            if(isPresent==undefined){
               let obj={...action.payload,qty:1}

                basketObj=[...state.cart,obj]
            }else{
                basketObj=increaseQuantity(state.cart,action)
            }
            for(let i=0;i<state.data.length;i++){
                let obj=state.data[i]
                if(state.data[i].id==action.payload.id){
                    obj={...state.data[i],qty:state.data[i].qty+1}
                    temp[i]=obj
                }else{
                    temp[i]=obj
                }
            }
            return {
                ...state,
                data:arr,
                qty:state.qty+1,
                cart:basketObj
            }
        case "removeFromBasket":
            console.log(action);
        default:
            return state
    }
}

function increaseQuantity(array,action){
    let temp=[]
    for(let i=0;i<array.length;i++){
        let obj=array[i]
        if(array[i].id==action.payload.id){
            obj={...array[i],qty:array[i].qty+1}
            temp[i]=obj
        }else{
            temp[i]=obj
        }
    }
    return temp
}
export default home
