import React from 'react'
import { useDispatch } from 'react-redux';
import './css/Home.css'
export default function Rows({data}) {
    // const state=useSelector(state=>state.home)
    console.log(data);

    const dispatch = useDispatch();
    const addBasket = (obj) => {
      dispatch({
        type: "addInBasket",
        payload: obj,
      });
    };
    const removeBasket = (obj) => {
      dispatch({
        type: "removeFromBasket",
        payload: obj,
      });
    };
    return (
        <>
             {data.map((obj) => {
          return (
            <div className="catagoryRows">
              <img src={obj.filename} />
              <h6>{obj.title}</h6>
              <div className="qtyPrice">
                <span>
                  <b>price</b> : {obj.price}
                </span>
                <div className="rowsQty">
                  <h6>Qty : </h6>
                </div>
                <div className="addRemove">
                  <button
                    onClick={() => removeBasket(obj)}
                    className="btn btn-danger addCart"
                  >
                    -
                  </button>
                  <h6>{obj.qty}</h6>
                  <button
                    onClick={() => addBasket(obj)}
                    className="btn btn-primary addCart add"
                  >
                    +
                  </button>
                </div>
              </div>
            </div>
          );
        })}
        </>
    )
}
