import jwt_decode from "jwt-decode"
let initialState = {
    currUser: jwt_decode(localStorage.getItem("jwt")).id,
};

const auth = (state = initialState, action) => {
    console.log(state.currUser);
    console.log(state.data);
    switch (action.type) {

        case "login":
            let j = jwt_decode(action.payload.token)
            console.log("token decoded", j);
            localStorage.setItem("jwt", action.payload.token)
            return {
                ...state,
                currUser: action.payload.user,
            };
        case "signup":
            let email = action.payload.email;
            let value = JSON.stringify(action.payload);
            localStorage.setItem(email, value);

        case "signout":
            return {
                ...state,
                currUser: null,
            };
        case "editUser":
            let newUser = action.payload
            return {
                ...state,
                currUser: newUser,
            };

        default:
            return state;
    }
};


export default auth;
