    const dispatch = useDispatch();
    const auth = useSelector(state => state.auth.currUser)
    const cart = useSelector(state => state.cart.cart)
    const history = useHistory()

    const addBasket = async (obj) => {
        if (auth) {
            let arr = [];
            let basketObj = [];
            let isPresent = cart.find((item) => {
                return item.id == obj.id
            });
            if (isPresent == undefined) {
                basketObj = [...cart, { ...obj, qty: 1 }]
            } else {
                basketObj = increaseQuantity(state.cart, action);
            }
            dispatch({
                type: "addInBasket",
                payload: obj,
            });
        } else {
            alert("please login first")
            history.push("/login")
        }
    };
    const removeBasket = (obj) => {
        if (auth) {
            dispatch({
                type: "removeFromBasket",
                payload: obj,
            });
        }
        else {
            alert("please login first")
            history.push("/login")
        }
    };
    const deleteBasket = (obj) => {

        if (auth) {
            dispatch({
                type: "deleteItem",
                payload: obj,
            });
        } else {
            alert("please login first")
            history.push("/login")
        }
    };
    return [addBasket, removeBasket, deleteBasket];
}
