  }
  const [state,setState]=useState({
      fname:user.fname,
      lname:user.lname,
      email:user.email,
      address:user.address
  })

  return (
    <div className={modalClass}>
      <div className="modalBox">
        <button onClick={hideModal} className="btn btn-danger closeBtn">X</button>
        <div>
          <h5>first name</h5>
          <input type="text" className="form-control" placeholder={user.fname} />
        </div>
        <div>
          <h5>Last name</h5>
          <input type="text"  class="form-control" placeholder={user.lname} />
        </div>
        <div>
          <h5>email</h5>
          <input type="text" class="form-control" placeholder={user.email} />
        </div>
        <div>
          <h5>address</h5>
          <input type="text" class="form-control" placeholder={user.address} />
        </div>
        <button className="btn btn-success " id="editConfirm">Submit</button>
      </div>
    </div>
  );
}
