import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { useLocation, useParams } from "react-router";
import "./css/itemDetails.css";
export default function ItemDetails() {
  const location = useLocation();
  const { data } = location.state;
  const selector=useSelector(state=>state.home)
  const dispatch = useDispatch();
//   const param=useParams()
//   const data=selector.data.find((obj)=>{
//       return obj.title==param.name
//   })
  console.log(data);
  const addBasket = (obj) => {
    dispatch({
      type: "addInBasket",
      payload: obj,
    });
  };
  const removeBasket = (obj) => {
    dispatch({
      type: "removeFromBasket",
      payload: obj,
    });
  };
  return (
    <div className="itemContainer">
      <div className="itemDetails">
        <img src={data.filename} />
        <h1>{data.title}</h1>
        <div>,
          <h5>Price : </h5>
          <span> {data.price}</span>
        </div>
        <div>
          <h5>Rating :</h5>
          <span> {data.rating}</span>
        </div>

        <div>{data.description}</div>
        <div className="addRemove">
                <h6>Qty : </h6>
                <button
                  onClick={() => removeBasket(data)}
                  className="btn btn-danger addCart"
                >
                  -
                </button>
                <h6>{data.qty}</h6>
                <button
                  onClick={() => addBasket(data)}
                  className="btn btn-primary addCart add"
                >
                  +
                </button>
              </div>
      </div>
    </div>
  );
}
