import data from "../groceries/data"
let initialState={
    data:data,
    search:"",
    qty:0,
    basket:[]
}
const home=(state=initialState,action)=>{
    switch (action.type) {
        case "basketQty":
            console.log(action);
            return {
                qty:state.qty+action.payload,
               ...state,
            }

        default:
            return state
    }

}
export default home
