import axios from 'axios';
import React from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { useHistory } from 'react-router-dom';

export default function useBasket() {
    const dispatch = useDispatch();
    const auth = useSelector(state => state.auth.currUser)
    const history = useHistory()
    const addBasket = async (obj) => {
        if (auth) {
            // let item = await axios.post("http://localhost:9000/cart", { obj })
            dispatch({
                type: "addInBasket",
                payload: obj,
            });
        } else {
            alert("please login first")
            history.push("/login")
        }
    };
    const removeBasket = (obj) => {
        if (auth) {
            dispatch({
                type: "removeFromBasket",
                payload: obj,
            });
        }
        else {
            alert("please login first")
            history.push("/login")
        }
    };
    const deleteBasket = (obj) => {

        if (auth) {

            dispatch({
                type: "deleteItem",
                payload: obj,
            });
        } else {
            alert("please login first")
            history.push("/login")
        }
    };
    return [addBasket, removeBasket, deleteBasket];
}
