import React from 'react'
import { useSelector,useDispatch } from 'react'

export const AddBasket = (obj) => {
    const dispatch = useDispatch();
    dispatch({
      type: "addInBasket",
      payload: obj,
    });
  };
  export const RemoveBasket = (obj) => {
    const dispatch = useDispatch();
    dispatch({
      type: "removeFromBasket",
      payload: obj,
    });
  };
