import React from 'react'
import { useSelector,useDispatch } from 'react'

export function AddBasket (obj){
    const select=useSelector(state=>state.home)
    const dispatch = useDispatch(select);
    dispatch({
      type: "addInBasket",
      payload: obj,
    });
  };
export function RemoveBasket  (obj) {
    const select=useSelector(state=>state.home)
    const dispatch = useDispatch(select);
    dispatch({
      type: "removeFromBasket",
      payload: obj,
    });
  };
