    return (
        <div className="star-rating">

            {[...Array(5)].map((star) => {
                let newClass = rating >= 0 ? "fa fa-star" : "fa fa-star checked";
                rating--;
                console.log(rating);
                return (
                    // <span className="star">&#9733;</span>

                    <span className={newClass}></span>
                );
            })}
        </div>
    );
}
