import React, { useState } from "react";
import { useDispatch } from "react-redux";
export default function Modal({ hideModal, modal, user }) {
  const modalClass = modal ? "modalContainer" : "modalContainer close";
  const dispatch = useDispatch();
  const editUser = () => {
    dispatch({});
  };
  const [state, setState] = useState({
    fname: user.fname,
    lname: user.lname,
    email: user.email,
    address: user.address,
  });
const changeInput=(e)=>{
    setState({ ...state, [e.target.name]: e.target.value });
}
  return (
    <div className={modalClass}>
      <div className="modalBox">
        <button onClick={hideModal} className="btn btn-danger closeBtn">
          X
        </button>
        <div>
          <h5>first name</h5>
          <input
            name="fname"
            type="text"
            className="form-control"
            placeholder={user.fname}
            value={state.fname}
          />
        </div>
        <div>
          <h5>Last name</h5>
          <input
          value={state.lname}
            name="lname"
            type="text"
            class="form-control"
            placeholder={user.lname}
          />
        </div>
        <div>
          <h5>email</h5>
          <input
            name="email"
            value={state.email}
            type="text"
            class="form-control"
            placeholder={user.email}
          />
        </div>
        <div>
          <h5>address</h5>
          <input
            name="address"
            value={state.address}
            type="text"
            class="form-control"
            placeholder={user.address}
            onChange={changeInput}
          />
        </div>
        <button className="btn btn-success " id="editConfirm">
          Submit
        </button>
      </div>
    </div>
  );
}
