import data from "../groceries/data"
let initialState={
    data:data,
    search:"",
    qty:0,
    basket:[]
}
const home=(state=initialState,action)=>{
    // console.log(action.payload);
    switch (action.type) {
        case "addInBasket":
            let arr=[]
            let basketObj=[...state.basket,action.payload]
            let isPresent=state.data.find(obj=>{
                return obj.id==action.payload.id
            })
            console.log(isPresent);
            for(let i=0;i<state.data.length;i++){
                if(state.data[i].id==action.payload.id){
                    let obj=state.data[i]
                    obj.qty++
                  arr.push(obj)
                //   arr[i].qty++
                   continue
                }
                arr.push(state.data[i])
            }
            console.log(arr);

            return {
                ...state,
                data:arr,
                qty:state.qty+1,
                basket:basketObj
            }
        case "removeFromBasket":
            console.log(action);
        default:
            return state
    }

}
export default home
