import Grocery from "../groceries/Grocery"

let initialState = {
    qty: 0,
    cart: []
}

const cart = (state = initialState, action) => {
    switch (action.type) {
        case "getCart":
            console.log(Grocery);

            return {
                qty: action.payload.length,
                cart: action.payload,
            }

        case "cartChange":
            console.log("inside reducer cart ", action.payload);
            return {
                qty: action.payload.length,
                cart: [...action.payload]
            };

        default: return state

    }
}




export default cart
