import data from "../groceries/data"
let initialState={
    data:data,
    search:"",
    qty:0,
    basket:[]
}
const home=(state=initialState,action)=>{
    console.log(action);
    switch (action.type) {
        case "basketQty":

            console.log(state.qty);
            return {
                ...state,
                qty:state.qty+1,
            }

        default:
            return state
    }

}
export default home
